function getQuote(e){const t=new Headers,n={method:"GET",headers:t,mode:"cors",cache:"no-store"},o=new Request(url),r=fetch(o,n).then(function(e){return e.json().then(function(e){console.log(e),renderQuote(e)})})}function renderQuote(e){quoteContainer.innerHTML=e[0].content}const quoteContainer=document.querySelector(".quote-text"),newQuoteBtn=document.querySelector(".new-quote-btn");newQuoteBtn.addEventListener("click",getQuote);const url="http://quotesondesign.com/wp-json/posts?filter[orderby]=rand&filter[posts_per_page]=1";